/**
 * 最大触摸时间
 */
export declare const maxTouchTime: number;
/**
 * 最大滑动切换图片距离
 */
export declare const maxMoveOffset: number;
/**
 * 图片的间隔
 */
export declare const horizontalOffset: number;
/**
 * 最小初始响应距离
 */
export declare const minStartTouchOffset: number;
/**
 * 默认背景透明度
 */
export declare const defaultOpacity: number;
/**
 * 最小缩放度
 */
export declare const minScale: number;
/**
 * 最大缩放度（若图片足够大，则会超出）
 */
export declare const maxScale: number;
/**
 * 滑动加速度
 */
export declare const slideAcceleration: number;
/**
 * 缩放弹性缓冲
 */
export declare const scaleBuffer: number;
